var hive_loader = require('hive-loader');
var Layout = require('layout');

var _mixins = {
	name:    'layout_handler',
	respond: function (params) {
		var lay = Layout(params.file_path);
		lay.init(params.gate.latch());
	}
};

module.exports = function(){
	return hive_loader.handler(_mixins, {dir: true, name_filter: /.*/i});
}